import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.map.js";
import { Loader } from '@googlemaps/js-api-loader';
import { searchLandsat } from '@/api/landsat';
import { sampleNdwi } from '@/utils/ndwi-client';
export default {
  name: 'MapView',
  props: {
    coords: {
      type: Object,
      required: true
    }
  },
  data() {
    return {
      map: null
    };
  },
  async mounted() {
    const {
      latitude: lat,
      longitude: lng
    } = this.coords;

    // 1️⃣ Cargar Google Maps y colocar marcador
    try {
      const google = await new Loader({
        apiKey: process.env.VUE_APP_GOOGLE_KEY,
        version: 'weekly'
      }).load();
      this.map = new google.maps.Map(document.getElementById('map'), {
        center: {
          lat,
          lng
        },
        zoom: 15,
        disableDefaultUI: true
      });
      new google.maps.Marker({
        position: {
          lat,
          lng
        },
        map: this.map
      });
    } catch (err) {
      console.warn('Google Maps no se pudo cargar:', err);
    }

    // 2️⃣ Obtener escena Landsat y construir URLs COG firmadas
    let meanNdwi = null;
    let status = 'Sin datos';
    try {
      const ls8 = await searchLandsat({
        lat,
        lng
      });

      // Extraer URL base sin parámetros
      const blobNir = ls8.assets.nir08.split('?')[0];
      const blobSwir = ls8.assets.swir16.split('?')[0];

      // Solicitar token SAS para acceso a los blobs
      const account = 'landsateuwest';
      const container = 'landsat-c2';
      const tokenResp = await fetch(`https://planetarycomputer.microsoft.com/api/sas/v1/token/${account}/${container}`);
      if (!tokenResp.ok) throw new Error(`Token SAS falla: ${tokenResp.status}`);
      const {
        token
      } = await tokenResp.json();

      // Construir URLs firmadas para cada banda
      const nirUrl = `${blobNir}?${token}`;
      const swirUrl = `${blobSwir}?${token}`;

      // 3️⃣ Leer pixel y calcular NDWI
      console.log("seva a llamar a sampleNdwi");
      meanNdwi = await sampleNdwi(nirUrl, swirUrl, lat, lng);
      if (meanNdwi !== null) {
        if (meanNdwi < 0.10) status = 'Seco';else if (meanNdwi < 0.25) status = 'Medio';else status = 'Húmedo';
      }
    } catch (e) {
      console.error('Error calculando NDWI:', e);
    }

    // 4️⃣ Emitir evento con estado y valor NDWI
    console.log(meanNdwi, status);
    this.$emit('ndwi-status', {
      ndwi: meanNdwi,
      status
    });
  }
};
