import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.map.js";
import { Loader } from '@googlemaps/js-api-loader';
import { searchLandsat } from '@/api/landsat';
import { sampleNdwi } from '@/utils/ndwi-client';
export default {
  name: 'MapView',
  props: {
    coords: {
      type: Object,
      required: true
    }
  },
  data() {
    return {
      map: null
    };
  },
  async mounted() {
    const {
      latitude: lat,
      longitude: lng
    } = this.coords;

    // 1Ô∏è‚É£ Cargar Google Maps y colocar marcador
    try {
      const google = await new Loader({
        apiKey: process.env.VUE_APP_GOOGLE_KEY,
        version: 'weekly'
      }).load();
      this.map = new google.maps.Map(document.getElementById('map'), {
        center: {
          lat,
          lng
        },
        zoom: 15,
        disableDefaultUI: true
      });
      new google.maps.Marker({
        position: {
          lat,
          lng
        },
        map: this.map
      });
    } catch (err) {
      console.warn('Google Maps no se pudo cargar:', err);
    }

    // 2Ô∏è‚É£ Obtener escena Landsat y construir URLs COG firmadas
    let meanNdwi = null;
    let status = 'Sin datos';
    try {
      const ls8 = await searchLandsat({
        lat,
        lng
      });

      // Construir URLs base sin par√°metros (split '?')
      const blobNir = ls8.assets.nir08.split('?')[0];
      const blobSwir = ls8.assets.swir16.split('?')[0];

      // Solicitar token SAS para el container completo
      const account = 'landsateuwest';
      const container = 'landsat-c2';
      const tokenResp = await fetch(`https://planetarycomputer.microsoft.com/api/sas/v1/token/${account}/${container}`);
      if (!tokenResp.ok) throw new Error(`Token SAS falla: ${tokenResp.status}`);
      const {
        token
      } = await tokenResp.json();

      // URLs completas con SAS token
      const nirUrl = `${blobNir}?${token}`;
      const swirUrl = `${blobSwir}?${token}`;
      console.log('üîó nirUrl:', nirUrl);
      console.log('üîó swirUrl:', swirUrl);

      // 3Ô∏è‚É£ Intentar leer pixel y calcular NDWI con GeoTIFF
      console.log('‚ñ∂Ô∏è Llamando a sampleNdwi‚Ä¶');
      meanNdwi = await sampleNdwi(nirUrl, swirUrl, lat, lng);
      console.log('‚Üê sampleNdwi result:', meanNdwi);

      // 4Ô∏è‚É£ Si NDWI es null, intentar fallback con STAC /aggregate
      if (meanNdwi === null) {
        console.log('‚ö†Ô∏è NDWI null; intentando fallback STAC aggregate‚Ä¶');
        const px = 30 / 111_320;
        const geom = {
          type: 'Polygon',
          coordinates: [[[lng - px, lat - px], [lng - px, lat + px], [lng + px, lat + px], [lng + px, lat - px], [lng - px, lat - px]]]
        };
        const urlAgg = `https://planetarycomputer.microsoft.com/api/data/v1/collections/landsat-c2-l2` + `/items/${encodeURIComponent(ls8.itemId)}/aggregate` + `?assets=nir08,swir16` + `&expression=${encodeURIComponent('(nir08 - swir16)/(nir08 + swir16)')}` + `&geom=${encodeURIComponent(JSON.stringify(geom))}` + `&${token}`;
        console.log('‚û∞ URL fallback aggregate:', urlAgg);
        const aggRes = await fetch(urlAgg);
        const aggJson = await aggRes.json();
        meanNdwi = aggJson.result?.mean ?? aggJson.mean ?? null;
        console.log('‚Üê aggregate result:', meanNdwi);
      }

      // 5Ô∏è‚É£ Clasificar estado
      if (meanNdwi !== null) {
        if (meanNdwi < 0.10) status = 'Seco';else if (meanNdwi < 0.25) status = 'Medio';else status = 'H√∫medo';
      }
    } catch (e) {
      console.error('Error calculando NDWI:', e);
    }

    // 6Ô∏è‚É£ Emitir evento con estado y valor NDWI
    console.log('‚ú® Emitiendo ndwi-status:', meanNdwi, status);
    this.$emit('ndwi-status', {
      ndwi: meanNdwi,
      status
    });
  }
};
