import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.map.js";
import { Loader } from '@googlemaps/js-api-loader';
import { searchLandsat } from '@/api/landsat';
import { sampleNdwi } from '@/utils/ndwi-client';
export default {
  name: 'MapView',
  props: {
    coords: {
      type: Object,
      required: true
    }
  },
  data() {
    return {
      map: null
    };
  },
  async mounted() {
    const {
      latitude: lat,
      longitude: lng
    } = this.coords;

    // 1️⃣ Cargar Google Maps y colocar marcador
    try {
      const google = await new Loader({
        apiKey: process.env.VUE_APP_GOOGLE_KEY,
        version: 'weekly'
      }).load();
      this.map = new google.maps.Map(document.getElementById('map'), {
        center: {
          lat,
          lng
        },
        zoom: 15,
        disableDefaultUI: true
      });
      new google.maps.Marker({
        position: {
          lat,
          lng
        },
        map: this.map
      });
    } catch (err) {
      console.warn('Google Maps no se pudo cargar:', err);
    }

    // 2️⃣ Obtener escena Landsat y construir URLs COG firmadas dinámicamente
    let meanNdwi = null;
    let status = 'Sin datos';
    try {
      const ls8 = await searchLandsat({
        lat,
        lng
      });

      // Extraer URL base (sin query) para NIR y SWIR
      const blobNir = ls8.assets.nir08.split('?')[0];
      const blobSwir = ls8.assets.swir16.split('?')[0];

      // Solicitar token SAS para cada blob
      const tokenRes = await Promise.all([fetch(`https://planetarycomputer.microsoft.com/api/sas/v1/token?resource=${encodeURIComponent(blobNir)}`), fetch(`https://planetarycomputer.microsoft.com/api/sas/v1/token?resource=${encodeURIComponent(blobSwir)}`)]);
      const [{
        token: tokenNir
      }, {
        token: tokenSwir
      }] = await Promise.all(tokenRes.map(res => {
        if (!res.ok) throw new Error(`Token SAS falla: ${res.status}`);
        return res.json();
      }));

      // Construir URLs con SAS
      const nirUrl = `${blobNir}?${tokenNir}`;
      const swirUrl = `${blobSwir}?${tokenSwir}`;

      // 3️⃣ Leer pixel y calcular NDWI
      meanNdwi = await sampleNdwi(nirUrl, swirUrl, lat, lng);
      if (meanNdwi !== null) {
        if (meanNdwi < 0.10) status = 'Seco';else if (meanNdwi < 0.25) status = 'Medio';else status = 'Húmedo';
      }
    } catch (e) {
      console.error('Error calculando NDWI:', e);
    }

    // 4️⃣ Emitir evento con estado y valor NDWI
    this.$emit('ndwi-status', {
      ndwi: meanNdwi,
      status
    });
  }
};
