import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.map.js";
import { Loader } from '@googlemaps/js-api-loader';
import { searchLandsat } from '@/api/landsat';
import { sampleNdwi } from '@/utils/ndwi-client';
export default {
  name: 'MapView',
  props: {
    coords: {
      type: Object,
      required: true
    }
  },
  data() {
    return {
      map: null
    };
  },
  async mounted() {
    const {
      latitude: lat,
      longitude: lng
    } = this.coords;

    // 1️⃣ Cargar Google Maps y colocar marcador
    try {
      const google = await new Loader({
        apiKey: process.env.VUE_APP_GOOGLE_KEY,
        version: 'weekly'
      }).load();
      this.map = new google.maps.Map(document.getElementById('map'), {
        center: {
          lat,
          lng
        },
        zoom: 15,
        disableDefaultUI: true
      });
      new google.maps.Marker({
        position: {
          lat,
          lng
        },
        map: this.map
      });
    } catch (err) {
      console.warn('Google Maps no se pudo cargar:', err);
    }

    // 2️⃣ Calcular NDWI usando geotiff.js
    let meanNdwi = null;
    let status = 'Sin datos';
    try {
      // Buscar escena Landsat y obtener URLs firmadas
      const ls8 = await searchLandsat({
        lat,
        lng
      });
      const nirUrl = `${ls8.assets.nir08}?${ls8.sas}`;
      const swirUrl = `${ls8.assets.swir16}?${ls8.sas}`;

      // Leer pixel y calcular NDWI
      meanNdwi = await sampleNdwi(nirUrl, swirUrl, lat, lng);
      if (meanNdwi !== null) {
        if (meanNdwi < 0.10) status = 'Seco';else if (meanNdwi < 0.25) status = 'Medio';else status = 'Húmedo';
      }
    } catch (e) {
      console.error('Error calculando NDWI:', e);
    }

    // 3️⃣ Emitir evento con el estado y valor NDWI
    this.$emit('ndwi-status', {
      ndwi: meanNdwi,
      status
    });
  }
};
